let send_redirect = function () {
  parent.postMessage( JSON.stringify( {'redirect': 'order_successful' } ), '*' );
}

if ( window.addEventListener ) {
  window.addEventListener( 'load', send_redirect, false );
} else if ( window.attachEvent ) { // ie8
  window.attachEvent( 'onload', send_redirect );
}

